import { useLocalStorage } from "react-use";
import axios from "axios";

import "../signin.css";
import { useEffect, useState } from "react";

export default function Index() {
  const [login, setLogin] = useLocalStorage("login", false);
  const [admin, setAdmin] = useLocalStorage("admin", false);
  const [users, setUsers] = useState("");

  useEffect(() => {
    if (!admin || !login) location.href = "/admin/login";

    axios.get("/api/users").then(r => setUsers(r.data));
  });

  return (
    <section>
      <nav
        style={{ position: "absolute", top: "0", width: "100%" }}
        className="navbar navbar-expand-md navbar-light bg-light"
      >
        <a className="navbar-brand" href="#">
          Homvery
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <form className="form-inline my-2 my-lg-0 ml-auto">
            <button
              className="btn btn-outline-success my-2 my-sm-0"
              onClick={() => {
                localStorage.clear();
                location.href = "/admin/login";
              }}
            >
              Logout
            </button>
          </form>
        </div>
      </nav>
      <section
        style={{ width: "100vw", textAlign: "center" }}
        className="text-center vsc-initialized"
      >
        <form className="form-signin">
          <i width="72" height="72" className="mb-4 material-icons">
            home
          </i>
          <h1 className="h3 mb-3 font-weight-normal">Users</h1>
          {users
            ? users.map((user, index) => (
                <a href={`/admin/tasks?id=${index}`} key={index}>
                  {user.phone}
                </a>
              ))
            : "Loading ..."}
          <p className="mt-5 mb-3 text-muted">Â© 2019 - Homvery</p>
        </form>
      </section>
    </section>
  );
}
